import React, { useEffect, useState } from 'react'

function HookTimer() {
  const [timer,setTimer] = useState(0);
  const increment =()=>{
     useState((timer) => timer+1);
  }
  useEffect(()=>{
       const interval = setInterval(increment,1000);
       return () =>{
         clearInterval(interval);
       };
  },[])  
  return (
    <div>
        <h1>{timer}</h1>
    </div>
  )
}

export default HookTimer
