import React, { useState } from 'react'

const MouseContainer = () => {
    const [display ,setDisplay] = useState(false);
    const changeDisplay=()=>{
        setDisplay(display =>!display);
    }
  return (
    <div>
        <button onClick={changeDisplay}>
          Toggle Display
        </button>
    </div>
  )
}

export default MouseContainer
