import React, { useState } from 'react'

const HookCounter = () => {
  const [counter,setCounter] = useState(0); 

  increment=()=>{
    setCounter((counter)=>counter+1);
  }
  return (
    <div>
       <button onClick={()=>setCounter(counter+1)}>Count {counter}</button>
    </div>
  )
}

export default HookCounter
